package Semana03_QuebraVigenere;

public class QuebraVigenere {
    private static final String CRIPTOGRAMA = "";

    public static void main(String[] args) {
        for (int tamanhoSenha = 1 ; tamanhoSenha < 20 ; tamanhoSenha++) {
            System.out.println();
            System.out.println("========================");
            for (int numeroConjunto = 0 ; numeroConjunto < tamanhoSenha ; numeroConjunto++) {
                int[] vetorOcorrencias = new int[256]; //tamanho da tabela ASCII
                for (int i = 0 ; i < CRIPTOGRAMA.length() ; i+=2) {
                    if (((i/2) % tamanhoSenha) == numeroConjunto) {
                        vetorOcorrencias[Integer.parseInt(
                                CRIPTOGRAMA.substring(i, i + 2), 16)]++;
                    }
                }
                int letra = 0;
                int maior = 0;
                for (int i = 0; i < vetorOcorrencias.length ; i++) {
                    if (vetorOcorrencias[i] > maior) {
                        maior = vetorOcorrencias[i];
                        letra = i;
                    }
                }
                System.out.print((char) (letra ^ 32)); //32 -> espa√ßo em branco na tabela ASCII
            }
        }
    }

}
